mapper.select:
select
    a.id as customerId,
    a.type as type,
    a.avatar as avatar,
    a.name as name,
    a.nick_name as nickName,
    a.jim_name as jimName,
    a.sex as sex,
    a.age as age,
    a.working_age as workingAge,
    a.height as height,
    a.weight as weight,
    a.email as email,
    a.bank as bank,
    a.number as number,
    a.phone as phone,
    a.password as password,
    a.score as score,
    a.car_total as carTotal,
    a.rate as rate,
    a.on_line_time as onLineTime,
    a.login_date as loginDate,
    a.province as province,
    a.province_code as provinceCode,
    a.city as city,
    a.city_code as cityCode,
    a.area as area,
    a.area_code as areaCode,
    a.address as address,
    a.lon_lat as lonLat,
    a.current_address as currentAddress,
    a.content as content,
    a.code as code,
    a.open_id as openId,
    a.qrcode as qrcode,
    a.pro_code as proCode,
    a.up_id as upId,
    a.money as money,
    a.integral as integral,
    a.start_time as startTime,
    a.end_time as endTime,
    a.open_flag as openFlag,
    a.watch_flag as watchFlag,
    a.push_url as pushUrl,
    a.bond as bond,
    a.bond_flag as bondFlag,
    a.contract_flag as contractFlag,
    a.bond_file as bondFile,
    a.is_auth as isAuth,
    a.is_driver as isDriver,
    a.driving_license as drivingLicense,
    a.insurance as insurance,
    a.insurance_certificate as insuranceCertificate,
    a.status as status,
    a.create_by as createBy,
    a.create_time as createTime,
    a.update_by as updateBy,
    a.update_time as updateTime,
    a.remark as remark,
    a.del_flag as delFlag
from pro_customer a

     and a.type = #{type}
     and a.avatar = #{avatar}
     and a.name like concat('%', #{name}, '%')
     and a.nick_name like concat('%', #{nickName}, '%')
     and a.jim_name like concat('%', #{jimName}, '%')
